import React, { Fragment } from 'react';

import DEFAULT_IMAGE from './layout/default.jpg';
const Movie = ({ movie }) => {
  const { Poster, Title, Year, imdbID } = movie;
  const poster = Poster === 'N/A' ? DEFAULT_IMAGE : Poster;
  return (
    <Fragment>
      <div className='movie col-lg-3 col-md-3 col-sm-6 col-xs-12 '>
        <img src={poster} className='card-img-top' alt={Title} />
        <div className='movie-body'>
          <h2 className='card-title'>{Title}</h2>
          <span>Year: {Year}</span>
          <a href='#imdbID' className='btn openbtn'>
            Open Dialog
          </a>
        </div>
      </div>

      <div className='wrapper'>
        <div className='modal' id={imdbID}>
          <div className='modal-content'>
            <div className='movie col-lg-12 col-md-8 col-sm-6 col-xs-12 '>
              <img src={poster} className='card-img-top' alt={Title} />
              <div className='movie-body'>
                <h2 className='card-title'>{Title}</h2>
                <span>Year: {Year}</span>
              </div>
            </div>
            <a href='#close' className='btn closebtn'>
              &times;
            </a>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default Movie;
