import React from 'react';
import DEFAULT_IMAGE from './layout/default.jpg';
import { Link } from 'react-router-dom';

const Movie = ({ movie }) => {
  const { Poster, Title } = movie;
  const poster = Poster === 'N/A' ? DEFAULT_IMAGE : Poster;
  return (
    <div className='movie col-lg-3 col-md-3 col-sm-6 col-xs-12 animated fadeInDown'>
      <img src={poster} className='card-img-top' alt={Title} />
      <div className='movie-body'>
        <h2 className='card-title'>{Title}</h2>
        <Link to={`/movie/${imdbID}`} className='details'>
          Details
        </Link>
      </div>
    </div>
  );
};

export default Movie;
