import React, { Fragment, useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import './App.css';
import MovieList from './components/MovieList';
import Search from './components/Search';
import Alert from './components/layout/Alert';

const App = () => {
  // console.log(process.env.API);
  // const API = process.env.API;
  const API = 'http://www.omdbapi.com/?i=tt3896198&apikey=933802f4';
  const [movies, setMovies] = useState([]);
  const [singleMovie, setSingleMovie] = useState({});
  const [loading, setLoading] = useState(true);
  const [alert, setAlert] = useState(null);

  //Get Movies
  useEffect(() => {
    setLoading(true);
    fetch(`${API}&s=dragon ball`)
      .then((response) => response.json())
      .then((jsonResponse) => {
        setMovies(jsonResponse.Search);
        setLoading(false);
      });
  }, []);
  // Search movies
  const searchMovies = (searchTerm) => {
    setLoading(true);
    fetch(`https://www.omdbapi.com/?s=${searchTerm}&apikey=933802f4`)
      .then((response) => response.json())
      .then((jsonResponse) => {
        setMovies(jsonResponse.Search);
        setLoading(false);
      });
  };

  // const getMovie = () => {
  //   setLoading(true);
  //   fetch(`http://www.omdbapi.com/?i=${imdbID}&apikey=933802f4`)
  //     .then((response) => response.json())
  //     .then((jsonResponse) => {
  //       setSingleMovie(jsonResponse.Search);
  //       setLoading(false);
  //     });
  // };

  // Set Alert
  const showAlert = (msg) => {
    setAlert({ msg });
    setTimeout(() => setAlert(null), 4000);
  };

  return (
    <Router>
      <Fragment>
        <Navbar />
        <div className='container'>
          <h1 className='text-center title'>React Movie List</h1>
          <Alert alert={alert} />
          <Search searchMovies={searchMovies} setAlert={showAlert} />
          <MovieList movies={movies} loading={loading} />
        </div>
      </Fragment>
    </Router>
  );
};

export default App;
